// Parse incoming VAPI order request
const webhookData = $input.first().json;
const body = webhookData.body || webhookData;
const toolCalls = body.message?.toolCalls || [];
const toolCallId = toolCalls[0]?.id || '';
const argsRaw = toolCalls[0]?.function?.arguments || '{}';
const args = typeof argsRaw === 'string' ? JSON.parse(argsRaw) : argsRaw;

// Extract order details
const order = {
  toolCallId,
  customerName: args.customer_name || 'Customer',
  customerPhone: args.customer_phone || body.message?.call?.customer?.number || '',
  orderType: args.order_type || 'pickup',
  deliveryAddress: args.delivery_address || '',
  items: args.items || '',
  total: args.total || 0,
  specialInstructions: args.special_instructions || '',
  orderTime: new Date().toLocaleString('en-US', { timeZone: 'America/New_York' }),
  orderId: 'PF-' + Date.now().toString(36).toUpperCase()
};

// Format phone number
let phone = order.customerPhone.replace(/[^0-9+]/g, '');
if (phone && !phone.startsWith('+')) {
  phone = phone.length === 10 ? '+1' + phone : '+' + phone;
}
order.formattedPhone = phone;

// Calculate estimated time
order.estimatedTime = order.orderType === 'pickup' ? '20-25 minutes' : '35-45 minutes';

return { json: order };

